// import React from 'react'

// const Contact = () => {
//   return (
//     <div>Contact</div>
//   )
// }

// export default Contact


import React, { useRef } from 'react';
import emailjs from 'emailjs-com';
import '../styles/Contact.css';

const Contact = () => {
    const form = useRef();

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs.sendForm(
        'service_3zfp61k', 
        'template_bbfy4ia',
         form.current,
          'PFcb1LzjcIuVRzoPS')
      e.target.reset()
  };
  return (
    <section className='contact section' id='contact'>
        <h2 className='section__title'>Get in touch</h2>
       <span className='section__subtitle'>Contact Me</span> 
        

        <div className='contact__container container grid'>
            <div className='contact__content'>
                <h3 className='contact__title'>Talk to me</h3>

                <div className='contact__info'>
                    <div className='contact__card'>
                        <i className='bx bx-mail-send contact__card-icon'></i>

                        <h3 className='contact__card-title'>Email</h3>
                        <span className='contact__card-data'>ramasaikondasani@gmail.com</span>

                        <a href='mailto:examplemail@gmail.com.com' className='contact__button'>Write me {' '}
                        <i className='bx bx-right-arrow-alt contact__button-icon'></i></a>
                    </div>

                    <div className='contact__card'>
                        <i className='bx bxl-whatsapp contact__card-icon'></i>

                        <h3 className='contact__card-title'>Whatapp</h3>
                        <span className='contact__card-data'>+91 9490222268</span>

                        <a href='https://api.whatsapp.com/send?phone=62214408789&text=Hello, more information!' className='contact__button'>Write me {' '}
                        <i className='bx bx-right-arrow-alt contact__button-icon'></i></a>
                    </div>

                    <div className='contact__card'>
                        <i className='bx bxl-linkedin contact__card-icon'></i>

                        <h3 className='contact__card-title'>Linkedin</h3>
                        <span className='contact__card-data'>rama-sai</span>

                        <a href='https://www.linkedin.com/home' className='contact__button'>Write me {' '}
                        <i className='bx bx-right-arrow-alt contact__button-icon'></i></a>
                    </div>
                </div>
            </div>

            <div className='contact__content'>
                <h3 className='contact__title'>Write me your project</h3>

                <form ref={form} onSubmit={sendEmail} className='contact__form'>
                    <div className='contact__form-div'>
                        <label className='contact__form-tag'>Name</label>
                        <input type='text' name='name'
                         className='contact__form-input' placeholder='Insert your name'/>
                    </div>

                    <div className='contact__form-div'>
                        <label className='contact__form-tag'>Mail</label>
                        <input type='email' name='email'
                         className='contact__form-input' placeholder='Insert your email'/>
                    </div>

                    <div className='contact__form-div contact__form-area'>
                        <label className='contact__form-tag'>Project</label>
                        <textarea name='project' cols={30} rows={10}
                        className='contact__form-input' placeholder='Write your project'></textarea>
                    </div>

                    <button className='button button--flex'>
                        Send 
                       
                     </button>
                </form>
            </div>
        </div>
    </section>
  )
}

export default Contact
